conda create -n ncbi -c bioconda entrez-direct

conda activate ncbi

ncbi-genome-download --assembly-level complete vertebrate_other,vertebrate_mammalian,invertebrate --parallel 4 --format fasta

esearch -db assembly -query "GCF_037993035.2" | esummary | xtract -pattern DocumentSummary -element Organism

set -euo pipefail

BASE_DIR="${1:-.}"
cd "$BASE_DIR" || exit 1

for dir in GCF_*; do
    [[ -d "$dir" ]] || continue
    acc="${dir%%/}"

    echo " Processing $acc ..."

    # Get organism name from NCBI
    species=$(esearch -db assembly -query "$acc" \
        | esummary \
        | xtract -pattern DocumentSummary -element Organism)

    if [[ -z "$species" ]]; then
        echo "    No species name found for $acc, skipping."
        continue
    fi

    # Clean up name (spaces and parentheses  underscores)
    species_clean=$(echo "$species" | sed 's/[ ()]/_/g')

    # Find the .fna.gz file inside
    fna_file=$(find "$dir" -maxdepth 1 -type f -name "*.fna.gz" | head -n 1)
    if [[ -z "$fna_file" ]]; then
        echo "    No .fna.gz file found in $dir"
        continue
    fi

    ext="${fna_file##*.}"
    new_name="${dir}/${species_clean}_${acc}.fna.gz"

    mv "$fna_file" "$new_name"
    echo "   Renamed to: $(basename "$new_name")"
done

